'                and h.owner = :MW_SCHEMA',
'                and  a.project_id = :MW_PROJ_ID',
'                and   a.dbid = x.dbid) VlD,',
'         ((select count(*) cnt',
'               from   wwv_mig_acc_tables a',
'               where  a.PROJECT_ID = :MW_PROJ_ID',
'               and    a.dbid = x.dbid) - ',
'               (select count(decode( h.constraint_type,''P'',1)) cnt',
'                from',
'                wwv_mig_acc_tables a, sys.dba_constraints h',
'                where h.table_name = upper(wwv_mig_acc_load.transform_identifier(a.tblname))',
'                and h.constraint_type = ''P''',
'                and h.owner = :MW_SCHEMA',
'                and  a.project_id = :MW_PROJ_ID)) INVL,',
'          (select distinct count(status) cnt from wwv_mig_rev_tables',
'           where project_id = :MW_PROJ_ID',
'           and dbid = x.dbid',
'           and status = ''INCLUDE'') incl',
'  from   wwv_mig_access x',
