'           ((select count(*) cnt',
'            from   wwv_mig_acc_queries',
'            where  PROJECT_ID = :MW_PROJ_ID',
'            and dbid = j.dbid)-(select count(decode(h.status,''VALID'',1)) cnt ',
'             from ',
'            wwv_mig_acc_queries a, sys.dba_objects h',
'            where h.object_name = upper(wwv_mig_acc_load.transform_identifier(a.qryname))',
'            and h.owner = :MW_SCHEMA',
'            and a.project_id = :MW_PROJ_ID',
'            and a.security_group_id =  :flow_security_group_id',
'           and a.dbid = j.dbid)) invl,',
