'           where project_id = :MW_PROJ_ID',
'           and dbid = j.dbid',
'           and status = ''INCLUDE'') incl',
'  from wwv_mig_access j',
'  where j.project_id = :MW_PROJ_ID',
'  and j.security_group_id = :flow_security_group_id',
'  ',
'  UNION ALL',
'  ',
'  select k.dbid,',
