'         (select count(*) cnt',
'          from   wwv_mig_acc_forms',
'          where  PROJECT_ID = :MW_PROJ_ID',
'          and    DBID = k.DBID) cnt,',
'         (select count(decode(h.status,''VALID'',1))+ count(decode(h.status,null,decode(i.source_status,''VALID'',1))) valid',
'           from ((',
'           wwv_mig_acc_forms a LEFT JOIN wwv_mig_rev_forms i',
'           on a.project_id = i.project_id',
'           and a.dbid = i.dbid',
'           and a.formid = i.formid)',
'           LEFT JOIN sys.dba_objects h',
'           on UPPER(h.object_name) = UPPER(i.source_name)',
'           and h.owner = UPPER(:MW_SCHEMA))',
'           where a.project_id = :MW_PROJ_ID',
'           and a.security_group_id =  :flow_security_group_id) vld,',
'         (select (count(decode(h.status,''INVALID'',1))+ count(decode(h.status,null,decode(i.source_status,''INVALID'',1,null,1)))) invalid',
'           from ((',
'           wwv_mig_acc_forms a LEFT JOIN wwv_mig_rev_forms i',
'           on a.project_id = i.project_id',
'           and a.dbid = i.dbid',
'           and a.formid = i.formid)',
'           LEFT JOIN sys.dba_objects h',
'           on UPPER(h.object_name) = UPPER(i.source_name)',
'           and h.owner = UPPER(:MW_SCHEMA))',
'           where a.project_id = :MW_PROJ_ID',
'           and a.security_group_id =  :flow_security_group_id) invl,',
'          (select distinct count(status) cnt from wwv_mig_rev_forms',
'            where project_id = :MW_PROJ_ID',
'            and dbid = k.dbid',
'            and status = ''INCLUDE'') incl',
'  from wwv_mig_access k',
'  where k.project_id = :MW_PROJ_ID',
'  and k.security_group_id = :flow_security_group_id',
