'          wwv_flow_lang.system_message(''QUICK_APP_REPORTS'') label,',
'          ''f?p=4400:20:''||:app_session||''::NO:RIR:P20_DBID:''||l.dbid file_name_link,',
'          (select count(*) cnt',
'           from   wwv_mig_acc_reports',
'           where  PROJECT_ID = :MW_PROJ_ID',
'           and    DBID = l.dbid) cnt,',
'          (select count(decode(h.status,''VALID'',1))+ count(decode(h.status,null,decode(i.source_status,''VALID'',1))) valid   ',
'            from ((',
'            wwv_mig_acc_reports a LEFT JOIN wwv_mig_rev_reports i',
'            on a.project_id = i.project_id',
'            and a.dbid = i.dbid',
'            and a.reportid = i.reportid)',
'            LEFT JOIN sys.dba_objects h',
'            on UPPER(h.object_name) = UPPER(i.source_name)',
'            and h.owner = UPPER(:MW_SCHEMA))',
'            where a.project_id = :MW_PROJ_ID',
'            and a.security_group_id =  :flow_security_group_id',
'            and a.dbid = l.dbid) vld,',
'          (select (count(decode(h.status,''INVALID'',1))+ count(decode(h.status,null,decode(i.source_status,''INVALID'',1,null,1)))) invalid   ',
'            from ((',
'            wwv_mig_acc_reports a LEFT JOIN wwv_mig_rev_reports i',
'            on a.project_id = i.project_id',
'            and a.dbid = i.dbid',
'            and a.reportid = i.reportid)',
'            LEFT JOIN sys.dba_objects h',
'            on UPPER(h.object_name) = UPPER(i.source_name)',
'            and h.owner = UPPER(:MW_SCHEMA))',
'            where a.project_id = :MW_PROJ_ID',
'            and a.security_group_id =  :flow_security_group_id',
'            and a.dbid = l.dbid)  invl,',
'           (select distinct count(status) cnt from wwv_mig_rev_reports',
'            where project_id = :MW_PROJ_ID',
'            and dbid = l.dbid',
'            and status = ''INCLUDE'') incl',
'  from wwv_mig_access l',
