wwv_flow_api.create_page_process(
 p_id=>wwv_flow_api.id(22751016896638805.4401)
,p_process_sequence=>50
,p_process_point=>'AFTER_SUBMIT'
,p_process_type=>'NATIVE_PLSQL'
,p_process_name=>'Compile All Invalid SQL Queries'
,p_process_sql_clob=>wwv_flow_utilities.join(wwv_flow_t_varchar2(
'DECLARE',
'',
'  l_status   varchar2(32767);',
'  l_syntax   varchar2(32767);',
'  l_ans      number := 0;',
'  l_length   number := 0;',
'  l_dbid     number := 0;',
'',
'BEGIN',
'',
'  for c2 in (',
'       select dbid from wwv_mig_access',
'       where project_id = :MW_PROJ_ID',
'       and security_group_id = :flow_security_group_id',
'       and linkdbid is null',
'  )',
'  loop',
'    l_dbid := c2.dbid;',
'  end loop;',
'',
'  -- ',
'  -- Get list of SQL Queries and associated formids',
'  --',
'  for c1 in',
'  (',
'  	select source_syntax, formid  ',
'        from wwv_mig_rev_forms',
'        where source_type = ''SQL Query''',
'  	and project_id = :MW_PROJ_ID',
'  	and dbid = l_dbid',
'  	and security_group_id = :flow_security_group_id',
'  	and owner = :MW_SCHEMA  ',
'  )',
'  loop',
'    if (c1.source_syntax is not null) then',
'',
'         -- Remove trailing semi-colon from syntax',
'         l_length := LENGTH(c1.source_syntax);     ',
'         l_ans := INSTR(c1.source_syntax,chr(59),l_length -1,1);',
'         if l_ans <> 0 then',
'            l_syntax := SUBSTR(c1.source_syntax,0,l_length -1);',
'         else ',
'            l_syntax := c1.source_syntax;',
'         end if; ',
'',
'         l_status := wwv_render_report3.is_valid_query(',
'                       p_plug_source => l_syntax,',
'                       p_plug_source_type => ''SQL_QUERY'',',
'                       p_owner => :MW_SCHEMA);',
'',
'         if length(l_status) is null then',
' ',
'           -- Save syntax',
'           update wwv_mig_rev_forms',
'           set source_syntax = l_syntax',
'           where formid = c1.formid',
'           and project_id = :MW_PROJ_ID',
'           and security_group_id = :flow_security_group_id',
'           and dbid = l_dbid',
'           and owner = :MW_SCHEMA;',
'',
'           -- Save status of syntax     ',
'           update wwv_mig_rev_forms',
'           set source_status = ''VALID''',
'           where formid = c1.formid',
'           and project_id = :MW_PROJ_ID',
'           and security_group_id = :flow_security_group_id',
'           and dbid = l_dbid',
'           and owner = :MW_SCHEMA;   ',
'',
'         end if;',
'',
'    end if;',
'',
'  end loop;',
'',
'END;'))
,p_process_when=>'COMPILE_ALL'
,p_process_when_type=>'REQUEST_EQUALS_CONDITION'
);
