wwv_flow_api.create_page_process(
 p_id=>wwv_flow_api.id(6756306677836331.4401)
,p_process_sequence=>20
,p_process_point=>'AFTER_SUBMIT'
,p_process_type=>'NATIVE_PLSQL'
,p_process_name=>'Parse XML data into Forms Table (XMLDOM)'
,p_process_sql_clob=>wwv_flow_utilities.join(wwv_flow_t_varchar2(
'declare',
'    l_charset   varchar2(255);',
'    l_file_name varchar2(255);',
'    l_clob      clob            := EMPTY_CLOB;',
'begin',
'',
'    begin',
'        for c1 in (select file_content, file_char_set, file_name ',
'                   from   wwv_mig_forms',
'                   where  id  = :P52_ID)',
'        loop',
'            l_charset   := coalesce(c1.file_char_set, sys.owa_util.get_cgi_env(''REQUEST_IANA_CHARSET''), ''utf-8'');',
'            l_file_name := c1.file_name;',
'',
'            sys.dbms_lob.createTemporary(l_clob, false);',
'',
'            l_clob    := wwv_flow_utilities.blob_to_clob (',
'                               p_blob    => c1.file_content,',
'                               p_charset => l_charset);',
'',
'            update wwv_mig_forms',
'               set xml_content = XMLTYPE(l_clob)',
'             where id = :P52_ID;',
'',
'            sys.dbms_lob.freetemporary(l_clob);',
'        end loop;',
'        commit;',
'',
'    exception',
'        when others then',
'            delete wwv_mig_forms where id = :P52_ID;',
'            commit;',
'            wwv_flow_error.raise_internal_error (',
'              p_error_code => wwv_flow_lang.system_message(''F4400.XML_PROCESSING_ERROR'', wwv_flow_escape.html(l_file_name) ));',
'    end;',
'',
'    begin',
'        if (wwv_mig_frm_load_xml.is_valid_forms_xml(',
'                                p_file_id => :P52_ID,',
'                                p_project_id => :MW_PROJ_ID,',
'                                p_security_group_id => :flow_security_group_id)) then        ',
'',
'            for c1 in (select count(*) c from wwv_mig_forms where id = :P52_ID)',
'            loop',
'                if c1.c > 0 then',
'                    begin',
'                        wwv_mig_frm_load_xml.load_all_nodes (',
'                            p_file_id => :P52_ID);',
'                    exception',
'                        when others then',
'                            delete wwv_mig_forms where id = :P52_ID;',
'                            commit;',
'                            wwv_flow_error.raise_internal_error ( p_error_code => wwv_flow_lang.system_message(''F4400.XML_PARSING_ERROR'', wwv_flow_escape.html(l_file_name)) );',
'                    end;',
'                end if;',
'            end loop;',
'',
'        else',
'            delete wwv_mig_forms where id = :P52_ID;',
'            commit;',
'            apex_application.g_unrecoverable_error := true;',
'            wwv_flow_error.raise_internal_error ( p_error_code => wwv_flow_lang.system_message(''F4400.XML_PARSING_ERROR'', wwv_flow_escape.html(l_file_name)) );',
'        end if;',
'    end;',
'end;'))
,p_process_error_message=>'El archivo XML de Forms no se ha cargado.'
,p_process_when=>'P52_FILE_TYPE'
,p_process_when_type=>'VAL_OF_ITEM_IN_COND_EQ_COND2'
,p_process_when2=>'FMB'
,p_process_success_message=>'El archivo XML de Forms se ha cargado correctamente.'
);
