'    l_count          number;',
'    l_arr_count      number;',
'',
'begin',
'',
'  -- Set Unchecked CheckBoxes to''N'' and set Modified ITEM_PROMPT columns',
'  FOR i IN 1 .. HTMLDB_APPLICATION.g_f04.COUNT',
'    LOOP',
'        ',
'        -- Set ITEM_PROMPT Only For Changed Values',
'        UPDATE WWV_MIG_FRM_REV_BLOCKS',
'           SET name       = wwv_flow.g_f05(i)',
'         WHERE id         = wwv_flow.g_f04(i)',
'           AND security_group_id = :flow_security_group_id',
'           AND nvl2(name,name,'' '') <> nvl(wwv_flow.g_f05(i),'' '')',
'        RETURNING id INTO l_id;',
'         ',
'        if (l_id <> 0) then',
'            if (instr(l_ids,l_id) = 0) or (instr(l_ids,l_id) is null) then',
'                l_ids := l_ids||l_id||'':'';',
'            end if;',
'            l_id := 0;',
'        end if;',
'         ',
'        -- Create string of all IDs for all checkboxes',
'        l_list_vals := l_list_vals||wwv_flow.g_f04(i)||'':'';',
'',
'  END LOOP;',
'',
'  -- Set Selected CheckBoxes to ''Y''',
'  FOR i IN 1 .. HTMLDB_APPLICATION.g_f01.COUNT',
'    LOOP',
'      l_split := instr(wwv_flow.g_f01(i),'':'');',
'      if (l_split = 0) then',
' ',
'          UPDATE WWV_MIG_FRM_REV_BLOCKS',
'             SET select_for_app    =  ''Y''',
'           WHERE id                =  wwv_flow.g_f01(i)',
'             AND security_group_id =  :flow_security_group_id',
'             AND select_for_app    <> ''Y''',
'          RETURNING id into l_id;',
'',
'          if (l_id <> 0) then',
'              if (instr(l_ids,l_id) = 0) or (instr(l_ids,l_id) is null) then',
'                  l_ids := l_ids||l_id||'':'';',
'              end if;',
'              l_id := 0;',
'          end if;',
