'',
'  -- Remove Trailing semi-colon from string of IDs',
'  l_vals := substr(l_ids,0,length(l_ids) - 1);',
'  -- Convert String of IDs into ARRAY',
'  l_arr := wwv_flow_utilities.string_to_table(l_ids);',
'  l_count := l_arr.count;',
'',
' -- apex_util.set_session_state(''P54_MESSAGE'',l_count||'' Row(s) updated.'');',
'',
'  ',
'end;'))
,p_process_error_message=>'La información de bloque no se ha actualizado correctamente.'
,p_process_when_button_id=>wwv_flow_api.id(268941328234597551.4401)
