,p_process_sql_clob=>wwv_flow_utilities.join(wwv_flow_t_varchar2(
'declare',
'    l_split          number                   := 0;',
'    l_list_vals      varchar2(32767)          := null;',
'    l_vals           varchar2(32767)          := null;',
'    l_arr            wwv_flow_global.vc_arr2;',
'    l_arr2           wwv_flow_global.vc_arr2;',
'    l_ids            varchar2(32767)          := null;',
'    l_id             number                   := null;',
'    l_count          number                   := 0;',
'    l_arr_count      number;',
'    l_exists         number                   := 0;',
'',
'begin',
'',
'  -- Set Unchecked CheckBoxes to''N'' and set Modified ITEM_PROMPT columns',
'  FOR i IN 1 .. HTMLDB_APPLICATION.g_f04.COUNT',
'    LOOP',
'        ',
'        -- Set ITEM_PROMPT Only For Changed Values',
'        UPDATE WWV_MIG_FRM_REV_BLK_ITEMS',
'           SET item_prompt       = wwv_flow.g_f05(i)',
'         WHERE id                = wwv_flow.g_f04(i)',
'           AND security_group_id = :flow_security_group_id',
'           AND nvl2(item_prompt,item_prompt,'' '') <> nvl(wwv_flow.g_f05(i),'' '')',
'        RETURNING id INTO l_id;',
'',
'  END LOOP;',
'',
'   -- Set Checked Items to ''Y'' in tables, only updating changed rows',
'  FOR i IN 1 .. HTMLDB_APPLICATION.g_f01.COUNT',
'    LOOP',
'      l_split := instr(wwv_flow.g_f01(i),'':'');',
'      if (l_split = 0) then',
' ',
'          UPDATE WWV_MIG_FRM_REV_BLK_ITEMS',
'             SET select_for_app    =  ''Y''',
'           WHERE id                =  wwv_flow.g_f01(i)',
'             AND security_group_id =  :flow_security_group_id',
'             AND select_for_app    <> ''Y''',
'          RETURNING id into l_id;',
'      end if;',
'  END LOOP;',
'',
'   FOR i IN 1 .. HTMLDB_APPLICATION.g_f03.COUNT',
'     LOOP',
'     -- All items have been selected',
'        FOR ii IN 1 .. HTMLDB_APPLICATION.g_f01.COUNT LOOP',
'           if HTMLDB_APPLICATION.g_f03(i) = HTMLDB_APPLICATION.g_f01(ii) then',
'                l_exists := 1;',
'                exit;',
'           else',
'                l_exists := 0;',
'           end if;',
'        END LOOP;',
'',
'        -- Build up array of de-selected checkboxes  ',
'        if l_exists = 0 then',
'           l_count := l_count + 1;',
'           l_arr2(l_count) := HTMLDB_APPLICATION.g_f03(i);',
'        end if;',
'',
'   END LOOP;',
'',
'   FOR iii in 1 .. l_arr2.count loop',
'    UPDATE wwv_mig_frm_rev_blk_items',
'       SET select_for_app = ''N''',
'     WHERE id = l_arr2(iii)',
'       AND  select_for_app <> ''N''',
'     RETURNING id INTO l_id;',
'   end loop;',
'  ',
'end;'))
