,p_pre_element_text=>'<pre class="code_text">'
,p_post_element_text=>wwv_flow_utilities.join(wwv_flow_t_varchar2(
'</pre>',
'<a href="javascript:copy_custom();"  class="aIconButton">',
'<img src="#IMAGE_PREFIX#f_spacer.gif" class="copyIcon" alt="COPY_QUERY" title="COPY_QUERY" /></a>'))
,p_source=>wwv_flow_utilities.join(wwv_flow_t_varchar2(
'declare',
'    l_status varchar2(4) := null;',
'    l_qry varchar2(32767) := null;',
'    l_block_source varchar2(32767) := :P77_QUERYDATASOURCENAME ;',
'begin',
'    if :P77_QUERYDATASOURCENAME is not null then',
'        l_qry := ''select '' || chr(10);',
'        for c1 in (select i.id, ',
'                          nvl(i.columnname,i.name) column_name,',
'                          i.id block_item_id,',
'                          decode(i.databaseitem, ''true'',  t.y,',
'                                                 ''false'', t.n,',
'                                                          nvl2(i.columnname,t.y,t.n)) databaseitem',
'                    from  wwv_mig_forms p, ',
'                          wwv_mig_frm_modules m, ',
'                          wwv_mig_frm_formmodules f, ',
'                          wwv_mig_frm_blocks b, ',
'                          wwv_mig_frm_blk_items i,',
'                          (select wwv_flow_lang.system_message(''APEXIR_YES'') y,',
'                                  wwv_flow_lang.system_message(''APEXIR_NO'') n ',
'                             from dual) t',
'                   where  p.id = m.file_id',
'                     and  m.id = f.module_id',
'                     and  f.id = b.formmodule_id',
'                     and  b.id = i.block_id',
'                     and  ((upper(i.databaseitem) = ''TRUE'' and i.itemtype <> ''Push Button'') or ',
'                          (i.databaseitem is null and (i.itemtype is null or i.itemtype <> ''Push Button'')))',
'                     and  p.project_id = :MW_PROJ_ID',
'                     and  b.id = :P77_ID',
'        ) loop',
'            l_qry := l_qry || l_status ||''    "''||trim(:P77_NAME)||''"."''|| trim(c1.column_name) || ''"'' ; ',
'            l_status := '',''||chr(10);',
'        end loop;',
'          -- code to check the Table name existing in in the schema, else use the upper case of that',
'           if wwv_flow_builder.is_valid_table_or_view(:MW_SCHEMA, trim(l_block_source)) = ''U'' then ',
'              l_block_source := upper(l_block_source);',
'           end if;        ',
'        l_qry := l_qry || chr(10) || '' from "'' || trim(l_block_source) ||''" "''|| trim(:P77_NAME) || ''"'';',
'    end if;',
'        ',
'    return l_qry;',
'end;'))
,p_source_type=>'FUNCTION_BODY'
,p_display_as=>'NATIVE_TEXTAREA'
,p_cSize=>80
,p_cMaxlength=>32767
,p_cHeight=>8
,p_read_only_when_type=>'ALWAYS'
,p_field_template=>wwv_flow_api.id(717589175640513847.4401)
