'declare',
'    l_session varchar2(255) := :APP_SESSION;',
'    l_security_group_id number := :FLOW_SECURITY_GROUP_ID;',
'    l_days    varchar2(255) := null;',
'    l_release varchar2(255) := :P4000_RELEASE;',
'begin',
'    -- milestones',
'    for c1 in ( select /* APEX4d004e42 */ count(*) milestones,',
'                    max(EVENT_DATE) max_date,',
'                    sum(decode(',
'                        greatest(trunc(sysdate),trunc(event_date)),',
'                        trunc(sysdate),0,1)) future_events',
'                from wwv_flow_events e',
'                where security_group_id = l_security_group_id',
'                    and (nvl(l_release,0) = 0',
'                        or release = l_release)',
'            ) loop',
'        if c1.milestones = 0  then',
'            sys.htp.p(''<div class="a-BadgeChart a-BadgeChart--justGageLike">'');',
