,p_plug_source=>wwv_flow_utilities.join(wwv_flow_t_varchar2(
'select event_date,',
'    days_out,',
'    event_name,',
'    targeted_features,',
'    f80_percent_or_better,',
'    features_complete,',
'    feature_owners,',
'    associated_todos,',
'    associated_bugs,',
'    id,',
'    ''f?p=''||:APP_ID||'':7:''||:APP_SESSION||'':::7,RP:P7_ID:''||id the_url,',
'    case',
'        when days_out < 0 and targeted_features > 0 and f80_percent_or_better = targeted_features',
'            then ''black''',
'            -- null?',
'        when days_out < 0 and targeted_features > 0 then ''red''',
'        when days_out < 0 and targeted_features = 0 then ''null''',
'        when days_out = 0 then ''yellow''',
'        else ''green''',
'    end as color',
'from (',
'    select /* APEX 4800P6006b */',
'        event_date,',
'        to_char(event_date,''Month'') the_month,',
'        to_char(event_date,''YYYY'') the_year,',
'        to_char(event_date,''DD'') the_day,',
'        trunc(event_date) - trunc(sysdate) days_out,',
'        event_name,',
'        (   select count(*) features',
'            from wwv_flow_features',
'            where event_id = e.id ) targeted_features,',
'        (   select count(*) features',
'            from wwv_flow_features',
'            where event_id = e.id',
'                and nvl(feature_status,0) >= 80 ) f80_percent_or_better,',
'        (   select count(*) features',
'            from wwv_flow_features',
'            where event_id = e.id',
'                and nvl(feature_status,0) = 100 ) features_complete,',
'        (   select count(distinct feature_owner) feature_owners',
'            from wwv_flow_features',
'            where event_id = e.id',
'                and feature_owner is not null ) feature_owners,',
'        (   select count(*)',
'            from wwv_flow_tasks',
'            where security_group_id = :FLOW_SECURITY_GROUP_ID',
'                and event_id = e.id ) associated_todos,',
'        (   select count(*)',
'            from wwv_flow_bugs',
'            where security_group_id = :FLOW_SECURITY_GROUP_ID',
'                and target_milestone_id = e.id) associated_bugs,',
'        id',
'    from wwv_flow_events e',
'    where security_group_id = :FLOW_SECURITY_GROUP_ID',
'        and (nvl(:P6006_SHOW,''0'')=''ALL'' or event_date >= trunc(sysdate) )',
'        and (:P6006_RELEASE = ''0'' or release = :P6006_RELEASE)',
'    )',
'order by event_date'))
