'<pre>',
'function my_authentication (',
'    p_username in varchar2,',
'    p_password in varchar2 )',
'    return boolean',
'is',
'    l_user my_users.user_name%type := upper(p_username);',
'    l_pwd  my_users.password%type;',
'    l_id   my_users.id%type;',
'begin',
