'declare',
'   l_report varchar2(32767);',
'   l_body   varchar2(32767);',
'   l_from   varchar2(255) := null;',
'   l_crlf constant varchar2(4) := unistr(''\000d'') || unistr(''\000a'');',
'begin',
'   -- determine from',
'   for c1 in (',
'       select email_address ',
'       from wwv_flow_fnd_user ',
'       where security_group_id = :flow_security_group_id and ',
'           user_name = :app_user) loop',
