'     l_from := :P76_TO;',
'   end if;',
'',
'   -- run report',
'   wwv_flow_workspace_reports.report_workspace_usage (',
'    p_security_group_id => :P76_WORKSPACE_ID,',
'    p_time_frame_DWMY => :P76_REPORTING_PERIOD,',
'    p_print_report_yn => ''N'',',
'    p_email_format_yn => ''Y'',',
'    p_date_format => :DATE_TIME_FORMAT);',
