'',
'   -- send email;',
'   APEX_MAIL.SEND(',
'     p_to          => :P76_TO,',
'     p_from        => l_from,',
'     p_body        => l_body,',
'     p_body_html   => l_report,',
'     p_subj        => :P76_SUBJECT,',
'     p_cc          => null,',
'     p_bcc         => null,',
'     p_replyto     => null);',
'',
'   -- push',
'   APEX_MAIL.PUSH_QUEUE;',
'end;'))
