'with sqlplan as (',
'    select object_type,OBJECT#,object_name,object_owner',
'      from sys.gv_$sql_plan',
'    where address =  :P551_ADDRESS',
'      and hash_value = :P551_HASH_VALUE',
'      and child_number = :P551_CHILD_NUMBER',
'      and inst_id = :P551_INST_ID',
'      and object_type in (''TABLE'',''INDEX'',''MATERIALIZED VIEW'',''INDEX (UNIQUE)'')),',
'ws_schemas as(',
'    select schema ',
'      from wwv_flow_company_schemas',
'     where security_group_id = :flow_security_group_id),',
't as(',
'        select object_owner table_owner,object_name table_name,',
'               OBJECT# OBJECT_ID',
'          from sqlplan',
'         where object_type in (''TABLE'',''MATERIALIZED VIEW'')',
'         union',
'        select d1.table_owner table_owner,d1.table_name table_name,',
'               d.OBJECT_ID',
'          from sys.dba_indexes d1, sqlplan s1,',
'               sys.dba_objects d',
'         where d1.index_name = s1.object_name',
'           and d1.owner = s1.object_owner',
'           and s1.object_type in(''INDEX'',''INDEX (UNIQUE)'')',
'           and d.object_type in (''TABLE'',''MATERIALIZED VIEW'')',
'           and d.owner = d1.table_owner',
'           and d.object_name = d1.table_name',
'           ),',
'cols as(',
'    select table_owner,index_name,',
'           rtrim(',
'           max(decode(position,1 ,column_name))||'',''||',
'           max(decode(position,2 ,column_name))||'',''||',
'           max(decode(position,3 ,column_name))||'',''||',
'           max(decode(position,4 ,column_name))||'',''||',
'           max(decode(position,5 ,column_name))||'',''||',
'           max(decode(position,6 ,column_name))||'',''||',
'           max(decode(position,7 ,column_name))||'',''||',
'           max(decode(position,8,column_name))',
'           ,'','') columns',
'     from(',
'        select c.table_owner,c.index_name,c.column_name,c.column_position position',
'          from sys.dba_ind_columns c, t',
'         where t.table_owner = c.table_owner',
'           and t.table_name = c.table_name)',
'    group by table_owner,index_name',
')',
'select di.owner, ',
'       case when di.owner in (select schema from ws_schemas) then',
'            ''<a href="f?p=4500:1001:''||:APP_SESSION||'':FOCUS:::OB_OBJECT_ID,OB_CURRENT_TYPE,OB_SCHEMA:''||t.object_id||'',TABLE,''|| wwv_flow_escape.html(di.owner)||''">''||wwv_flow_escape.html(di.table_name)||''</a>''',
'       else wwv_flow_escape.html(di.table_name) end table_name,',
'       case when di.owner in (select schema from ws_schemas) then',
'            ''<a href="f?p=4500:1001:''||:APP_SESSION||'':FOCUS:::OB_OBJECT_ID,OB_CURRENT_TYPE,OB_SCHEMA:''||do2.object_id||'',INDEX,''|| wwv_flow_escape.html(di.owner)||''">''||wwv_flow_escape.html(di.index_name)||''</a>''',
'       else wwv_flow_escape.html(di.index_name) end index_name,',
'       case when (select count(*) cnt ',
'                    from sqlplan sp1',
'                   where sp1.object_type like ''INDEX%''',
'                     and sp1.object_name = di.index_name) > 0 ',
'            then ''<span class="a-Icon icon-check"></span>''',
'       end check_mark,',
'      c.columns, uniqueness, di.status,',
'      index_type, partitioned, ',
'      funcidx_status, join_index,t.object_id, do2.object_id index_object_id',
'from   sys.dba_indexes di,sys.dba_objects do2,',
'       t,cols c',
'where di.table_owner = t.table_owner',
'  and di.table_name = t.table_name',
'  and index_type != ''LOB''',
'  and do2.object_type = ''INDEX''',
'  and do2.owner = di.owner',
'  and do2.object_name = di.index_name',
'  and c.table_owner = di.table_owner',
'  and c.index_name  = di.index_name'))
