,p_source=>wwv_flow_utilities.join(wwv_flow_t_varchar2(
'with index_cols as (',
'    select table_owner,',
'           index_name,',
'           uniqueness,',
'           status,',
'           index_type,',
'           temporary,',
'           partitioned,',
'           funcidx_status,',
'           join_index,',
'           max(decode(position, 1, column_name))||',
'           max(decode(position, 2, '', ''||column_name))||',
'           max(decode(position, 3, '', ''||column_name))||',
'           max(decode(position, 4, '', ''||column_name))||',
'           max(decode(position, 5, '', ''||column_name))||',
'           max(decode(position, 6, '', ''||column_name))||',
'           max(decode(position, 7, '', ''||column_name))||',
'           max(decode(position, 8, '', ''||column_name)) as columns',
'      from ( select :OBJECT_OWNER as table_owner,',
'                    dc.index_name,',
'                    di.uniqueness,',
'                    di.status,',
'                    di.index_type,',
'                    di.temporary,',
'                    di.partitioned,',
'                    di.funcidx_status,',
'                    di.join_index,',
'                    dc.column_name,',
'                    dc.column_position position',
'               from sys.user_ind_columns dc,',
'                    sys.user_indexes di',
'              where dc.table_name = :OBJECT_NAME',
'                and di.index_name = dc.index_name',
'           )',
'     group by table_owner,',
'              index_name,',
'              uniqueness,',
'              status,',
'              index_type,',
'              temporary,',
'              partitioned,',
'              funcidx_status,',
'              join_index',
')',
'select ic.table_owner as owner,',
'       ic.index_name,',
'       ic.columns,',
'       ic.uniqueness,',
'       ic.status,',
'       ic.index_type,',
'       ic.temporary,',
'       ic.partitioned,',
'       ic.funcidx_status,',
'       ic.join_index,',
'       do.object_id',
'  from index_cols ic,',
'       sys.user_objects do',
' where do.object_name = ic.index_name',
'   and do.object_type = ''INDEX'''))
