'   end loop;',
'else',
'   for r2 in ( select object_name,owner,object_type',
'               from  sys.dba_objects ',
'               where object_id = :OBJECT_ID and owner = :OB_SCHEMA ) loop',
'      :OBJECT_NAME  := r2.object_name;',
'      :OBJECT_OWNER := r2.owner;',
'      if(r2.object_type = ''PACKAGE BODY'') then ',
'        :OBJECT_TYPE  := ''PACKAGE'';',
'      else',
