'',
'       for r2 in ( select object_name,owner,object_type',
'                   from  sys.dba_objects ',
'                   where object_name = :OBJECT_NAME',
'                   and   OWNER = :OBJECT_OWNER',
'                   and   OBJECT_TYPE = ''PACKAGE BODY'' ) loop',
