'        :OBJECT_TYPE  := ''PACKAGE'';',
'      else',
'        :OBJECT_TYPE  := r2.object_type;',
'      end if;',
'   end loop;',
'   if ( :REQUEST = ''GETOBJECT_SPEC'' ) then',
'       for r2 in ( select object_name,owner,object_type',
