'with pkg_app_image as (',
'    select m.installed_ws_id,',
'           p.image_identifier image_class',
'    from wwv_flow_pkg_applications p, wwv_flow_pkg_app_map m',
'    where p.app_id = m.app_id',
'    and p.app_type = ''WS''',
'    and m.security_group_id = :flow_security_group_id',
'    )',
'select ',
'    id,',
'    p.image_class,',
'    case when p.image_class is null then ',
'        wwv_flow_4000_ui.get_app_initials( name, id )',
'    else',
'        '' ''',
'    end as app_initials,',
'    case when p.image_class is null then ',
'        ''u-ColorBG--'' || to_char( ora_hash( name, 29 ) + 1 )',
'    else',
'        '' ''',
'    end as app_color,',
'    name name,',
'    name app_name,',
'    owner,',
'    status build_status,',
'    (select count(*) from wwv_flow_ws_webpages where ws_app_id = f.id) page_count,',
'    updated_on updated,',
'    lower(updated_by) last_updated_by,',
'    created_on created,',
'    lower(created_by) created_by,',
'    ''<a  href="ws?p=''||f.id||'':home:''||:app_session||''" class="a-button"><span class="a-icon  icon-run-page" title="''||wwv_flow_lang.system_message(''run_application_id'', f.id)||''"></span></a>'' actions,',
'    ''f?p=4900:900:''||:app_session||''::::ws_app_id,p900_id:''||f.id||'',''||f.home_page_id home_link,',
'    description application_comments',
'from  wwv_flow_ws_applications f,',
'      pkg_app_image p',
'where f.security_group_id = :flow_security_group_id',
'and f.id = p.installed_ws_id (+)'))
