'declare',
'    l_app_type varchar2(30);',
'    l_apex_application_id number;',
'    l_apex_websheet_id number;',
'    l_sgid             number;',
'begin',
'',
'    if :P112_FILE_TYPE = ''FLOW_EXPORT'' then',
'        l_app_type := ''DB'';',
'        l_apex_application_id := :P109_NEW_APPLICATION_ID;',
'        l_apex_websheet_id := null;',
'    else',
'        l_app_type := ''WS'';',
'        l_apex_websheet_id := :P109_NEW_APPLICATION_ID;',
'        l_apex_application_id := null;',
