'    else',
'        l_sgid := 12;',
'    end if;',
'    ',
'    insert into wwv_flow_pkg_applications (',
'        app_id,',
'        app_name,',
'        app_description,',
'        app_type,',
'        app_group,',
'        apex_application_id,',
'        apex_websheet_id,',
'        app_status,',
'        build_version,',
'        app_category_id_1,',
'        unlock_allowed,',
'        security_group_id)',
'    values (',
'        :P109_NEW_APPLICATION_ID,',
'        :P109_NEW_APPLICATION_NAME,',
'        :P109_APP_DESCRIPTION,',
'        l_app_type,',
'        nvl(:P109_APP_GROUP,''CUSTOM''),',
