'        ''AVAILABLE'',',
'        1,',
'        :P109_CATEGORY,',
'        ''Y'',',
'        l_sgid);',
'        ',
'    -- save file',
'    for c1 in (select * ',
'               from wwv_flow_file_objects$',
'               where name = :P112_FILE',
'               and id = :P109_FILE_ID)',
'    loop',
'        insert into wwv_flow_pkg_app_files (',
'            app_id, ',
'            language,',
'            file_name, ',
'            mime_type,',
'            file_charset,',
'            blob_content,',
