,p_process_name=>'next and previous navigation'
,p_process_sql_clob=>wwv_flow_utilities.join(wwv_flow_t_varchar2(
'declare n varchar2(255); i number := 0;',
'begin',
'   select max(short_name) into n',
'   from WWV_FLOW_COMPANIES ',
'   where PROVISIONING_COMPANY_ID = :P8001_WORKSPACE_ID;',
'',
'if v(''REQUEST'') = ''NEXT'' then',
'   for c1 in (select PROVISIONING_COMPANY_ID',
'       from WWV_FLOW_COMPANIES',
'       where SHORT_NAME > n and PROVISIONING_COMPANY_ID not in (0,10,11)',
'       order by short_name) loop',
'       i := c1.PROVISIONING_COMPANY_ID;',
'       exit;',
'   end loop;',
'elsif v(''REQUEST'') = ''PREVIOUS'' then',
'   for c1 in (select PROVISIONING_COMPANY_ID',
